import { useEffect, useState } from "react";
import "./styles.css";

export default function App() {
  const [allWine, setAllWine] = useState([])

  useEffect(() => {
    fetch("/globalwinescores/latest/?limit=10", {
      "method": "GET",
      "headers": {
        "Authorization": "Token b4b3ed33237f19b10068eeced45359fb5c5929b1"
      }
   })
    .then((res) => res.json())
    .then(data => setAllWine(data["results"]))
  }, [])


  return (
    <div className="App">
      <header>
        <span>Global wine score app</span>
        <button>Login</button>
      </header>

      <body>
        <section>
          <h1>Display wine</h1>
          <div className="wine-description">

            {allWine.map(wine => {
              return (
                <li>
                <span>Name: {wine.wine}</span>
                <span>Country: {wine.country} </span>
                <span>Colour: {wine.color}</span>
                <span>Vintage: {wine.vintage}</span>
                <span>Score: {wine.score} </span>
              </li>
              ) 
            })}

        </div>
        </section>
      </body>
    </div>
  );
}
